// Lấy dữ liệu từ node HTTP Request
const items = $input.all();

// Xử lý từng mục (quảng cáo) trong mảng
return items.map(item => {
  const ad = item.json;
  const pageInfo = ad.pageInfo?.adLibraryPageInfo?.pageInfo || {};
  const snapshot = ad.snapshot || {};

  const body= snapshot.body || "NA";
  // Trích xuất image URLs (nối tất cả URL bằng dấu ;)
  const imageUrls = snapshot.images && snapshot.images.length > 0 
    ? snapshot.images.map(img => img.resizedImageUrl || '').join('; ') 
    : 'N/A';

  // Trích xuất video URLs (ưu tiên videoHdUrl, fallback videoSdUrl, thêm preview image nếu cần)
  const videoUrls = snapshot.videos && snapshot.videos.length > 0 
    ? snapshot.videos.map(video => {
        const hdUrl = video.videoHdUrl || '';
        const sdUrl = video.videoSdUrl || '';
        const previewUrl = video.videoPreviewImageUrl || '';
        // Ưu tiên HD, nếu không có thì dùng SD, và thêm preview URL
        return hdUrl || sdUrl || previewUrl || '';
      }).filter(url => url).join('; ')
    : 'N/A';

  return {
    json: {
      pageVerification: pageInfo.pageVerification,
      likes: pageInfo.likes,
      pageCategory: pageInfo.pageCategory,
      igUsername: pageInfo.igUsername,
      igFollowers: pageInfo.igFollowers ,
      startDate: ad.startDateFormatted || ad.startDate,
      adArchiveID: ad.adArchiveID || ad.adArchiveId,
      endDate: ad.endDateFormatted || ad.endDate,
      displayformat: snapshot.displayFormat,
      imageUrls: imageUrls,
      videoUrls: videoUrls,
      text: body.text,
      ctaType: snapshot.ctaType,

    }
  };
});

return items.map(item => {
  const imageUrls = item.json.imageUrls;

  // Tách chuỗi thành mảng dựa theo dấu chấm phẩy, đồng thời loại bỏ khoảng trắng dư
  const urls = imageUrls.split(';').map(url => url.trim()).filter(url => url !== '');

  return {
    json: {
      ...item.json,
      imageUrlsArray: urls, // mảng các URL
    }
  };
});

const ads = $('Loop Over Items').all(); // Dữ liệu từ node HTTP Request (quảng cáo)
const posts = $input.all(); // Dữ liệu từ node mới (bài viết)

return posts.map(post => {
  const postText = post.json.text || '';
  const matchedAd = ads.find(ad => ad.json.snapshot.body.text === postText);
  if (matchedAd) {
    return {
      json: {
        post_id: post.json.postId,
        adArchiveID: matchedAd.json.adArchiveID,
        text: postText
      }
    };
  }
  return null;
}).filter(item => item !== null);
